package com.bloxbean.cardano.yaci.core.model.serializers;

import com.bloxbean.cardano.yaci.core.model.Block;
import com.bloxbean.cardano.yaci.core.model.Era;
import com.bloxbean.cardano.yaci.core.util.HexUtil;
import lombok.extern.slf4j.Slf4j;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

@Slf4j
class BlockSerializerTest {

    @Test
    void deserializeDI() {
        String cborHex = "";
        var expectedBytes = HexUtil.decodeHexString(cborHex);

        Block block = BlockSerializer.INSTANCE.deserialize(expectedBytes);
        Assertions.assertNotNull(block);
        Assertions.assertEquals(Era.Babbage, block.getEra());

        var actualByes = BlockSerializer.INSTANCE.serialize(block);
        Assertions.assertEquals(expectedBytes, actualByes);
    }
}
